import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearchMinus, faSearchPlus, faTrash } from '@fortawesome/free-solid-svg-icons';

import './notesStyle.css';

const NoteItem = ({ note, onDelete, onToggleExpand,createdAt }) => {
  return (
    <div className="noteItem">

      <p>{note.expanded ? note.content : note.content.substring(0, 120) + '...'}</p>
      <div className="btnContainer">
        <p>Y la puta fecha?</p>
        {note.createdAt &&(
          <p>Creada:{new Date(note.createdAt).toLocaleString()}</p>
        )}
        <h3 className="expandButton" onClick={() => onToggleExpand(note.id)} style={{ cursor: 'pointer' }}>
          <span className="expandIcon">
            <FontAwesomeIcon icon={note.expanded ? faSearchMinus : faSearchPlus} />
          </span>

          {note.expanded ? 'Ver menos' : 'Ver más'}
        </h3>
        <button className="delButton" onClick={() => onDelete(note.id)}><span className="trashIcon"><FontAwesomeIcon icon={faTrash} /></span>Eliminar</button>

      </div>

    </div>
  );
};

export default NoteItem;
