import React from 'react';
import TaskItem from './TaskItem'; // Importamos el componente TaskItem
import "./ListStyle.css";

const TaskToDo = ({ tasks, onToggle, onDelete, onEdit }) => {//las ppropiedades como argumentos
    const tasksToDo = tasks.filter(task => !task.completed);

    return (
        <ul className="listaTareas">
            <li><h3>Tareas pendientes:</h3></li>
        {tasksToDo.map((task) => (
          <TaskItem
            key={task.id}
            task={task}
            onToggle={onToggle}
            onDelete={onDelete}
            onEdit={onEdit}
          />
        ))}
      </ul>

    );


};

export default TaskToDo;
