import React, { useState, useEffect } from 'react';
import TaskForm from './components/TaskForm';
import TaskToDo from './components/TaskListToDo';
import TaskDone from './components/TaskListDone';
import './App.css';

const App = () => {
  // Cargar las tareas desde LocalStorage o establecer un arreglo vacío
  const loadTasksFromLocalStorage = () => {
    const tasksFromStorage = localStorage.getItem('tasks');
    return tasksFromStorage ? JSON.parse(tasksFromStorage) : [];
  };

  const [tasks, setTasks] = useState(loadTasksFromLocalStorage()); // Inicializa las tareas desde LocalStorage
  const [showModal, setShowModal] = useState(false); // Estado para mostrar el modal

  // Guardar las tareas en LocalStorage cada vez que cambien
  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]); // Se ejecuta cada vez que las tareas cambian

  const addTask = (text) => {
    const newTask = {
      id: Date.now(),
      text,
      completed: false,
    };
    setTasks([...tasks, newTask]);
    setShowModal(false); // Cierra el modal después de añadir la tarea
  };

  const toggleTask = (id) => {
    setTasks(tasks.map(task =>
      task.id === id ? { ...task, completed: !task.completed } : task
    ));
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  const editTask = (id, newText) => {
    setTasks(
      tasks.map(task =>
        task.id === id ? { ...task, text: newText } : task
      )
    );
  };

  return (
    <div className="mainContainer">
      <h1>Gestor de Tareas</h1>
      <button className="openModalButton" onClick={() => setShowModal(true)}>
        ➕ Añadir Tarea
      </button>

        {/* MODAL */}
        {showModal && (
          <div className="modalOverlay">
            <div className="modalContent">
              <h2>Nueva Tarea</h2>
              <TaskForm onAdd={addTask} />
              <button onClick={() => setShowModal(false)} className="cancelButton">Cancelar</button>
            </div>
          </div>
        )}

        {/* TAREAS */}
        <div className="listContainer">
          <TaskToDo tasks={tasks.filter(task => !task.completed)} onToggle={toggleTask} onDelete={deleteTask}onEdit={editTask}/>
          <TaskDone tasks={tasks.filter(task => task.completed)} onToggle={toggleTask} onDelete={deleteTask}onEdit={editTask}/>

      </div>
    </div>
  );
};

export default App;
