import React, { useState } from 'react';
import "./ItemStyle.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPencil, faTrash, faCircleCheck, faThumbtack, faStopwatch } from '@fortawesome/free-solid-svg-icons';

function formatRealizationTime(ms) {
  const totalSeconds = Math.floor(ms / 1000);
  const days = Math.floor(totalSeconds / 86400);
  const hours = Math.floor((totalSeconds % 86400) / 3600);
  const minutes = Math.floor((totalSeconds % 3600) / 60);
  const seconds = totalSeconds % 60;

  return `${days}d ${hours}h ${minutes}m ${seconds}s`;
}

const TaskItem = ({ task, onToggle, onDelete, onEdit, editable = true }) => {
  const [isDeleting, setIsDeleting] = useState(false);

  return (
<li className="item">
  <div className="taskMain">
    <input
      type="checkbox"
      checked={task.completed}
      onChange={() => onToggle(task.id)}
      className="taskCheckbox"
    />

    <span
      className={`taskText ${task.completed ? 'completed' : ''}`}
    >
      {task.text}
    </span>
  </div>

  <div className="taskButtons">
    {editable && (
      <button
        className="editButton"
        onClick={() => onEdit(task.id, task.text)}
      >
        <FontAwesomeIcon icon={faPencil} className="pencilIcon" />
      </button>
    )}

    <button
      className={`dltButton ${isDeleting ? 'deleting' : ''}`}
      onMouseDown={() => {
        setIsDeleting(true);
        task._deleteTimeout = setTimeout(() => {
          onDelete(task.id);
          setIsDeleting(false);
        }, 800);
      }}
      onMouseUp={() => {
        clearTimeout(task._deleteTimeout);
        setIsDeleting(false);
      }}
      onMouseLeave={() => {
        clearTimeout(task._deleteTimeout);
        setIsDeleting(false);
      }}
      onTouchStart={() => {
        setIsDeleting(true);
        task._deleteTimeout = setTimeout(() => {
          onDelete(task.id);
          setIsDeleting(false);
        }, 800);
      }}
      onTouchEnd={() => {
        clearTimeout(task._deleteTimeout);
        setIsDeleting(false);
      }}
    >
      <FontAwesomeIcon icon={faTrash} className="trashIcon" />
      <div className="deleteBar"></div>
    </button>
  </div>

  <div className="taskMeta">
    {task.createdAt && (
      <p className="taskDate">
        <FontAwesomeIcon icon={faThumbtack} className="createdIcon" />
        Creada: {new Date(task.createdAt).toLocaleString()}
      </p>
    )}

    {task.completed && task.completedAt && (
      <p className="taskDate">
        <FontAwesomeIcon icon={faCircleCheck} className="checkIcon" />
        Realizada: {new Date(task.completedAt).toLocaleString()}
      </p>
    )}

    {task.completed && task.realizationTime && (
      <p className="taskDate">
        <FontAwesomeIcon icon={faStopwatch} className="timingIcon" />
        Tiempo: {formatRealizationTime(task.realizationTime)}
      </p>
    )}
  </div>
</li>

  );
};

export default TaskItem;
