import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { DragDropContext } from '@hello-pangea/dnd';

import TaskForm from './components/TaskForm';
import TaskToDo from './components/TaskListToDo';
import TaskDone from './components/TaskListDone';
import EditTaskModal from './components/EditTaskModal';
import NotesPage from './components/NotesPage';

import logoFull from './img/logoFull.png';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faXmark, faPlus, faFileLines } from '@fortawesome/free-solid-svg-icons';

import './App.css';

const App = () => {
  const loadTasksFromLocalStorage = () => {
    const tasksFromStorage = localStorage.getItem('tasks');
    return tasksFromStorage ? JSON.parse(tasksFromStorage) : [];
  };

  const [tasks, setTasks] = useState(loadTasksFromLocalStorage());
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  const addTask = (text) => {
    const newTask = {
      id: Date.now(),
      text,
      completed: false,
      createdAt: new Date().toISOString(),
      completedAt: null,
      realizationTime: null,
    };
    setTasks([...tasks, newTask]);
    setShowModal(false);
  };

  const toggleTask = (id) => {
    setTasks(prevTasks =>
      prevTasks.map(task => {
        if (task.id === id) {
          const completed = !task.completed;
          const now = new Date().toISOString();
          return {
            ...task,
            completed,
            completedAt: completed ? now : null,
            realizationTime: completed
              ? new Date(now) - new Date(task.createdAt)
              : null,
          };
        }
        return task;
      })
    );
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const activeTasks = tasks.filter(task => !task.completed);
    const reordered = Array.from(activeTasks);
    const [moved] = reordered.splice(result.source.index, 1);
    reordered.splice(result.destination.index, 0, moved);

    const otherTasks = tasks.filter(task => task.completed);
    setTasks([...reordered, ...otherTasks]);
  };

  return (
    <Router>
      <div className="mainContainer">
        <header>
          <img src={logoFull} alt="Logo TaskQuest" />
        </header>

        {/* Mostrar solo los botones de acceso */}
        <AccessButtons setShowModal={setShowModal} />

        {/* Modal para añadir tarea */}
        {showModal && (
          <div className="modalOverlay">
            <div className="modalContent">
              <button onClick={() => setShowModal(false)} className="cancelButton">
                <FontAwesomeIcon icon={faXmark} />
              </button>
              <h2>Añadir Nueva Tarea:</h2>
              <TaskForm onAdd={addTask} />
            </div>
          </div>
        )}

        {/* Rutas de la app */}
        <Routes>
          <Route path="/notas" element={<NotesPage />} />
          <Route path="/" element={
            <HomePage
              tasks={tasks}
              onToggle={toggleTask}
              onDelete={deleteTask}
              handleDragEnd={handleDragEnd}
            />
          } />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
