import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { } from '@fortawesome/free-solid-svg-icons';

import './notesStyle.css';

const NoteItem = ({ note, onDelete, onToggleExpand }) => {
  return (
    <div className="noteItem">

      <p>{note.expanded ? note.content : note.content.substring(0, 50) + '...'}</p>
      <h3 onClick={() => onToggleExpand(note.id)} style={{ cursor: 'pointer' }}>
  <FontAwesomeIcon icon={note.expanded ? faMagnifyingGlassMinus : faMagnifyingGlassPlus} style={{ marginRight: '0.5em' }} />
  {note.expanded ? 'Ver menos' : 'Ver más'}
</h3>
      <button onClick={() => onDelete(note.id)}>Eliminar</button>
    </div>
  );
};

export default NoteItem;
