import React, { useState, useEffect } from 'react';
import TaskForm from './components/TaskForm';
import TaskToDo from './components/TaskListToDo';
import TaskDone from './components/TaskListDone';

import "./App.css";

const App = () => {
  const [tasks, setTasks] = useState([]); // Estado para las tareas

  useEffect(() => {
    // Cargar las tareas desde localStorage al iniciar
    const storedTasks = JSON.parse(localStorage.getItem('tasks')) || [];
    console.log('Tareas cargadas desde localStorage:', storedTasks);  // Ver las tareas cargadas
    setTasks(storedTasks);
  }, []);  // Este useEffect solo se ejecuta una vez al inicio

  useEffect(() => {
    // Guardar las tareas en localStorage cuando cambien
    setTimeout(() => {
      console.log('Tareas guardadas en localStorage:', tasks);  // Ver qué tareas se están guardando
      localStorage.setItem('tasks', JSON.stringify(tasks));
    }, 100);  // 100ms de retraso (ajusta si es necesario)
  }, [tasks]);





  const addTask = (text) => {//Funcion de añadido
    const newTask = { id: Date.now(), text, completed: false };
    setTasks(prevTasks => [...prevTasks, newTask]);  // Asegúrate de que no estás sobrescribiendo las tareas
  };


  const toggleTask = (id) => {//Funcion de checkeo
    setTasks(
      tasks.map(task =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  const deleteTask = (id) => {//Funcion de borrado
    setTasks(tasks.filter(task => task.id !== id));
  };

  const editTask = (id, newText) => {
    setTasks(
      tasks.map(task =>
        task.id === id ? { ...task, text: newText } : task
      )
    );
  };

  return (
    <div className="mainContainer">
      <h1>Gestor de Tareas</h1>
      <button className="openModalButton" onClick={() => setShowModal(true)}>
        ➕ Añadir Tarea
      </button>

      {/* MODAL */}
      {showModal && (
        <div className="modalOverlay">
          <div className="modalContent">
            <h2>Nueva Tarea</h2>
            <TaskForm onAdd={addTask} />
            <button onClick={() => setShowModal(false)} className="cancelButton">Cancelar</button>
          </div>
        </div>
      )}

      {/* TAREAS */}
      <div className="listContainer">
        <TaskToDo tasks={tasks} onToggle={toggleTask} onDelete={deleteTask} />
        <TaskDone tasks={tasks} onToggle={toggleTask} />
      </div>
    </div>
  );
};


export default App;
