import React, { useState, useEffect } from 'react';
import TaskForm from './components/TaskForm';
import TaskToDo from './components/TaskListToDo';
import TaskDone from './components/TaskListDone';
import EditTaskModal from './components/EditTaskModal';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
<FontAwesomeIcon icon={faVolumeXmark} />


import './App.css';


const App = () => {
  const loadTasksFromLocalStorage = () => {
    const tasksFromStorage = localStorage.getItem('tasks');
    return tasksFromStorage ? JSON.parse(tasksFromStorage) : [];
  };

  const [tasks, setTasks] = useState(loadTasksFromLocalStorage());
  const [showModal, setShowModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editTaskId, setEditTaskId] = useState(null);
  const [editTaskText, setEditTaskText] = useState('');

  /* FUNCIONES-------------------------------------------------------------------------------------------------------------------- */ 

  /* UseEffect para manejar estados */ 
  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);


  /* Añadir tarea */ 
  const addTask = (text) => {
    const newTask = {
      id: Date.now(),
      text,
      completed: false,
      createdAt: new Date().toISOString(), // NUEVO: Fecha de asignación
      completedAt: null, // NUEVO: Fecha de realización (se queda null hasta completarse)
    };
    setTasks([...tasks, newTask]);
    setShowModal(false);
  };


  /* Toggle (cambio de ToDo a Done y viceversa) */ 
  const toggleTask = (id) => {
    setTasks(prevTasks =>
      prevTasks.map(task => {
        if (task.id === id) {
          const completed = !task.completed;
          return {
            ...task,
            completed,
            completedAt: completed ? new Date().toISOString() : null, // Asignamos o quitamos la fecha
          };
        }
        return task;
      })
    );
  };

  /* Borrar tarea */ 
  const deleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  /* Editar tarea */ 
  const editTask = (id, newText) => {
    setTasks(tasks.map(task =>
      task.id === id ? { ...task, text: newText } : task
    ));
    setShowEditModal(false);
  };

  /* Abrir Modal de Editar tarea */ 
  const openEditModal = (id, currentText) => {
    setEditTaskId(id);
    setEditTaskText(currentText);
    setShowEditModal(true);
  };

  /* -------------------------------------------------------------------------------------------------------------------- */ 

  return (
    <div className="mainContainer">
      <h1>Gestor de Tareas</h1>
      <button className="openModalButton" onClick={() => setShowModal(true)}>
        ➕ Añadir Tarea
      </button>

      {/* Modal para añadir tarea */}
      {showModal && (
        <div className="modalOverlay">
          <div className="modalContent">
            <button onClick={() => setShowModal(false)} className="cancelButton"><FontAwesomeIcon icon="fa-solid fa-volume-xmark" /></button>
            <h2>Añadir Nueva Tarea:</h2>
            <TaskForm onAdd={addTask} />
          </div>
        </div>
      )}

      {/* Modal para editar tarea */}
      {showEditModal && (
        <EditTaskModal
          initialText={editTaskText}
          onSave={(newText) => editTask(editTaskId, newText)}
          onCancel={() => setShowEditModal(false)}
        />
      )}

      {/* Tareas */}
      <div className="listContainer">
        <TaskToDo
          tasks={tasks.filter(task => !task.completed)}
          onToggle={toggleTask}
          onDelete={deleteTask}
          onEdit={openEditModal}
        />
        <TaskDone
          tasks={tasks.filter(task => task.completed)}
          onToggle={toggleTask}
          onDelete={deleteTask}
        />
      </div>
    </div>
  );
};

export default App;
