import React, { useState } from 'react';
import { Droppable, Draggable } from '@hello-pangea/dnd';
import TaskItem from './TaskItem';

const TaskToDo = ({ tasks, onToggle, onDelete, openEditModal }) => {
  const [isExpanded, setIsExpanded] = useState(false); // Controlamos si está expandida

  const toggleExpansion = () => setIsExpanded(!isExpanded);

  return (
    <Droppable droppableId="todo-tasks">
      {(provided) => (
        <div 
          className="list"
          {...provided.droppableProps}
          ref={provided.innerRef}
        >
          <h3 onClick={toggleExpansion} style={{ cursor: 'pointer' }}>
            Tareas pendientes: {isExpanded ? '↓' : '↑'}
          </h3>
          {isExpanded && (
            <ul className="listaTareas toDo" {...provided.droppableProps} ref={provided.innerRef}>
              {tasks.map((task, index) => (
                <Draggable key={task.id.toString()} draggableId={task.id.toString()} index={index}>
                  {(provided) => (
                    <li ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                      <TaskItem
                        task={task}
                        onToggle={onToggle}
                        onDelete={onDelete}
                        onEdit={openEditModal}
                      />
                    </li>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </ul>
          )}
        </div>
      )}
    </Droppable>
  );
};

export default TaskToDo;
