// HomePage.js
import React from 'react';
import TaskForm from './TaskForm';
import TaskToDo from './TaskListToDo';
import TaskDone from './TaskListDone';
import { DragDropContext } from '@hello-pangea/dnd';

const HomePage = ({ tasks, onToggle, onDelete, onEdit, handleDragEnd }) => {
  return (
    <div className="listContainer">
      <DragDropContext onDragEnd={handleDragEnd}>
        <TaskToDo
          tasks={tasks.filter(task => !task.completed)}
          onToggle={onToggle}
          onDelete={onDelete}
          onEdit={onEdit}
        />
      </DragDropContext>

      <TaskDone
        tasks={tasks.filter(task => task.completed)}
        onToggle={onToggle}
        onDelete={onDelete}
      />
    </div>
  );
};

export default HomePage;
