import "./ItemStyle.css";
//esto es algo parecido a una clase, pero no. Pero viene a servir de molde para crear una tarea

const TaskItem = ({ task, onToggle, onDelete, onEdit, editable = true }) => (//con sus propiedades
  <li className="item">
    <div className="taskControls">

      {/* Bot√≥n para eliminar */}
      <button className="dltButton" onClick={() => onDelete(task.id)}>üóëÔ∏è</button>

 {/* Bot√≥n para editar*/}
      {editable && (
        <button
          className="editButton"
          onClick={() => {
            const newText = prompt('Editar tarea:', task.text);
            if (newText && newText.trim() !== '') {
              onEdit(task.id, newText);
            } else {
              alert('No puedes dejar la tarea vac√≠a');
            }
          }}
        >
          üñäÔ∏è Editar
        </button>


      )}
      {/* Bot√≥n para editar MODAL*/}
      <button className="openModalButton" onClick={() => setShowModal(true)}>
      {showModal && (
          <div className="modalOverlay">
            <div className="modalContent">
            <button onClick={() => setShowModal(false)} className="cancelButton">X</button>
              <h2>A√±adir Nueva Tarea:</h2>

              <TaskForm onAdd={addTask} />

            </div>
          </div>
        )}

      üñäÔ∏è Editar(modal)
      </button>

      {/* El checkbox*/}
      <input
        type="checkbox"
        checked={task.completed}
        onChange={() => onToggle(task.id)} // Marca como completada
      />

      {/* El subrayado. S√≠, estilos*/}
      <span className="taskText" style={{


        textDecorationThickness: task.completed ? '4px' : 'none',
        color: task.completed ? '#888' : 'inherit'
      }}>
        {task.text}
      </span>

    </div>
  </li>
);

export default TaskItem;
