import React from 'react';
import "./ItemStyle.css";
//esto es algo parecido a una clase, pero no. Pero viene a servir de molde para crear una tarea

const TaskItem = ({ task, onToggle, onDelete, onEdit }) => (//con sus propiedades
  <li className="item">
    <div className="taskContent">
      {/* Bot√≥n para eliminar */}
      <button className="dltButton" onClick={() => onDelete(task.id)}>üóëÔ∏è</button> {/* Bot√≥n para eliminar */}

      <button className="editButton" onClick={() => {
        const newText = prompt('Editar tarea:', task.text); // Capturamos el nuevo texto
        if (newText && newText.trim() !== '') {  // Solo pasamos si el texto no est√° vac√≠o
          onEdit(task.id, newText);  // Llama a la funci√≥n de edici√≥n con el id y el nuevo texto
        } else {
          alert('No puedes dejar la tarea vac√≠a'); // Mensaje si el nuevo texto es vac√≠o
        }


      }}>
        üñäÔ∏è Editar
      </button>

      {/* El checkbox*/}
      <input
        type="checkbox"
        checked={task.completed}
        onChange={() => onToggle(task.id)} // Marca como completada
      />

      {/* El subrayado. S√≠, estilos*/}
      <span className="taskText"style={{

        textDecorationColor: task.completed ? '#55FF00' : 'none',
        textDecorationThickness: task.completed ? '4px' : 'none',
        color: task.completed ? '#ccc' : 'inherit'

      }}>
        {task.text}
      </span>
    </div>

  </li>
);

export default TaskItem;
