import React from 'react';
import "./ItemStyle.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPencil , faTrash , faCircleCheck, faThumbtack , faStopwatch} from '@fortawesome/free-solid-svg-icons';

function formatRealizationTime(ms) {
  const totalSeconds = Math.floor(ms / 1000);
  const days = Math.floor(totalSeconds / 86400);
  const hours = Math.floor((totalSeconds % 86400) / 3600);
  const minutes = Math.floor((totalSeconds % 3600) / 60);
  const seconds = totalSeconds % 60;

  return `${days}d ${hours}h ${minutes}m ${seconds}s`;
}

const TaskItem = ({ task, onToggle, onDelete, onEdit, editable = true, createdAt, completedAt }) => (
  <li className="item">
    <div className="taskControls">
      <button className="dltButton" onClick={() => onDelete(task.id)}><span className="trashIcon"><FontAwesomeIcon icon={faTrash} /></span></button>

      {/* Botón Editar solo si editable */}
      {editable && (
        <button
  className="editButton"
  onClick={() => onEdit(task.id, task.text)} // Llama correctamente a onEdit
>
  <span className="pencilIcon"><FontAwesomeIcon icon={faPencil} /></span>Editar
</button>

      )}

      <input
        type="checkbox"
        checked={task.completed}
        onChange={() => onToggle(task.id)}
      />

      <span className="taskText" style={{

        textDecorationThickness: task.completed ? '4px' : 'none',
        color: task.completed ? '#888' : 'inherit'
      }}>
        {task.text}
      </span>


    </div>

    {task.createdAt && (
        <p className="taskDate"> <span className="createdIcon"><FontAwesomeIcon icon={faThumbtack} /></span>Creada: {new Date(task.createdAt).toLocaleString()}</p>
      )}

      {task.completed && task.completedAt && (
        <p className="taskDate"><span className="checkIcon"><FontAwesomeIcon icon={faCircleCheck} /></span>Realizada: {new Date(task.completedAt).toLocaleString()}</p>
      )}
      {task.completed && task.realizationTime && (
  <p className="taskDate">
   <span className="timingIcon"> <FontAwesomeIcon icon={faStopwatch} /></span> Tiempo de realización: {formatRealizationTime(task.realizationTime)}
  </p>
)}

  </li>
);

export default TaskItem;
