import React from 'react';
import "./ItemStyle.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPencil} from '@fortawesome/free-solid-svg-icons';

const TaskItem = ({ task, onToggle, onDelete, onEdit, editable = true, createdAt, completedAt }) => (
  <li className="item">
    <div className="taskControls">
      <button className="dltButton" onClick={() => onDelete(task.id)}>üóëÔ∏è</button>

      {/* Bot√≥n Editar solo si editable */}
      {editable && (
        <button
          className="editButton"
          onClick={() => onEdit(task.id, task.text)} // Ya no hay prompt, solo modal
        >
         <FontAwesomeIcon icon={faPencil} />Editar
        </button>
      )}

      <input
        type="checkbox"
        checked={task.completed}
        onChange={() => onToggle(task.id)}
      />

      <span className="taskText" style={{

        textDecorationThickness: task.completed ? '4px' : 'none',
        color: task.completed ? '#888' : 'inherit'
      }}>
        {task.text}
      </span>


    </div>

    {!task.completed && task.createdAt && (
        <p className="taskDate"> Creada: {new Date(task.createdAt).toLocaleString()}</p>
      )}

      {task.completed && task.completedAt && (
        <p className="taskDate">‚úÖ Realizada: {new Date(task.completedAt).toLocaleString()}</p>
      )}
  </li>
);

export default TaskItem;
