import React, { useState, useEffect } from 'react';

const EditTaskModal = ({ taskId, currentText, onEdit, setShowEditModal }) => {
  const [newText, setNewText] = useState(currentText);

  const handleSave = () => {
    if (newText.trim()) {
      onEdit(taskId, newText);  // Guardar la tarea editada
    }
  };

  return (
    <div className="modalOverlay">
      <div className="modalContent">
        <button onClick={() => setShowEditModal(false)} className="cancelButton">
          X
        </button>
        <h2>Editar Tarea</h2>
        <input 
          type="text" 
          value={newText} 
          onChange={(e) => setNewText(e.target.value)} 
        />
        <button onClick={handleSave}>Guardar</button>
      </div>
    </div>
  );
};

export default EditTaskModal;
