import { Link } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import NoteItem from './NoteItem';
import NoteForm from './NoteForm';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHouse, faSquarePlus } from '@fortawesome/free-solid-svg-icons';

import './notesStyle.css';



const NotesPage = () => {
  const [notes, setNotes] = useState([]); // Lista de notas
  const [showNoteForm, setShowNoteForm] = useState(false);//Form de añadir nota

  const addNote = (noteContent) => {
    const newNote = {
      id: Date.now(),
      content: noteContent,
      expanded: false, // Estará contraído por defecto
      date: new Date().toISOString() // Agregar la fecha actual
    };
    setNotes([...notes, newNote]);
    setShowNoteForm(false);
  };
  // Cargar las notas desde localStorage cuando se monte el componente
  // Cargar las notas desde localStorage cuando se monte el componente
  useEffect(() => {
    const savedNotes = localStorage.getItem('notes');
    if (savedNotes) {
      setNotes(JSON.parse(savedNotes));
    }
  }, []);

  // Guardar las notas en localStorage solo si han cambiado
  useEffect(() => {
    const savedNotes = localStorage.getItem('notes');
    // Solo guardamos si las notas han cambiado realmente
    if (JSON.stringify(notes) !== savedNotes) {
      localStorage.setItem('notes', JSON.stringify(notes));
    }
  }, [notes]);

  //Borrar notas
  const deleteNote = (id) => {
    setNotes(notes.filter(note => note.id !== id));
  };
  //Expandir o contraer
  const toggleNoteExpand = (id) => {
    setNotes(notes.map(note =>
      note.id === id ? { ...note, expanded: !note.expanded } : note
    ));
  };

  return (
    <div className="main">
      <Link to="/">
        <button className="homeButton">
          <span className="homeIcon"><FontAwesomeIcon icon={faHouse} /></span>Inicio
        </button>
      </Link>
      <div className="notesContainer">
        <h2>Notas</h2>
        <button className="addNoteButton" onClick={() => setShowNoteForm(true)}><span className="addNoteIcon"><FontAwesomeIcon icon={faSquarePlus} /></span>Añadir Nota</button>

        {showNoteForm && <NoteForm onAdd={addNote} onClose={() => setShowNoteForm(false)} />}

        <div className="notesList">
          {notes.map(note => (
            <NoteItem
              key={note.id}
              note={note}
              onDelete={deleteNote}
              onToggleExpand={toggleNoteExpand}

            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default NotesPage;
