import React from 'react';
import "./ItemStyle.css";
//esto es algo parecido a una clase, pero no. Pero viene a servir de molde para crear una tarea

const TaskItem = ({ task, onToggle, onDelete, onEdit, editable = true }) => (//con sus propiedades
  <li className="item">
    <div className="taskControls">

      {/* Bot√≥n para eliminar */}
      <button className="dltButton" onClick={() => onDelete(task.id)}>üóëÔ∏è</button>

      {/* Bot√≥n para editar*/}
      {editable && (
        <button
          className="editButton"
          onClick={() => onEdit(task.id, task.text)} // ¬°sin prompt!
        >
          üñäÔ∏è Editar
        </button>
      )}


      {/* El checkbox*/}
      <input
        type="checkbox"
        checked={task.completed}
        onChange={() => onToggle(task.id)} // Marca como completada
      />

      {/* El subrayado. S√≠, estilos*/}
      <span className="taskText" style={{


        textDecorationThickness: task.completed ? '4px' : 'none',
        color: task.completed ? '#888' : 'inherit'
      }}>
        {task.text}
      </span>

    </div>
  </li>
);

export default TaskItem;
