import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

import TaskForm from './components/TaskForm';
import TaskToDo from './components/TaskListToDo';
import TaskDone from './components/TaskListDone';
import EditTaskModal from './components/EditTaskModal';
import NotesPage from './components/NotesPage';


import logoFull from './img/logoFull.png';

import { DragDropContext } from '@hello-pangea/dnd';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faXmark, faPlus, faFileLines } from '@fortawesome/free-solid-svg-icons';

import './App.css';


const App = () => {
  const loadTasksFromLocalStorage = () => {
    const tasksFromStorage = localStorage.getItem('tasks');
    return tasksFromStorage ? JSON.parse(tasksFromStorage) : [];
  };

  const [tasks, setTasks] = useState(loadTasksFromLocalStorage());
  const [showModal, setShowModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editTaskId, setEditTaskId] = useState(null);
  const [editTaskText, setEditTaskText] = useState('');


  /* FUNCIONES-------------------------------------------------------------------------------------------------------------------- */

  /* UseEffect para manejar estados */
  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);


  /* Añadir tarea */
  const addTask = (text) => {
    const newTask = {
      id: Date.now(),
      text,
      completed: false,
      createdAt: new Date().toISOString(), // NUEVO: Fecha de asignación
      completedAt: null, //Fecha de realización (se queda null hasta completarse)
      realizationTime: null,
    };
    setTasks([...tasks, newTask]);
    setShowModal(false);
  };


  /* Toggle (cambio de ToDo a Done y viceversa) */
  const toggleTask = (id) => {
    setTasks(prevTasks =>
      prevTasks.map(task => {
        if (task.id === id) {
          const completed = !task.completed;
          const now = new Date().toISOString();


          return {
            ...task,
            completed,
            completedAt: completed ? now : null,
            realizationTime: completed
              ? new Date(now) - new Date(task.createdAt)
              : null,
          };
        }
        return task;
      })
    );
  };

  /* Borrar tarea */
  const deleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  /* Editar tarea */
  const editTask = (id, newText) => {
    setTasks(tasks.map(task =>
      task.id === id ? { ...task, text: newText } : task
    ));
    setShowEditModal(false);
  };

  /* Abrir Modal de Editar tarea */
  const openEditModal = (id, currentText) => {
    setEditTaskId(id);
    setEditTaskText(currentText);
    setShowEditModal(true);
  };

  /* Lógica de arrastre */

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const activeTasks = tasks.filter(task => !task.completed);
    const reordered = Array.from(activeTasks);
    const [moved] = reordered.splice(result.source.index, 1);
    reordered.splice(result.destination.index, 0, moved);

    const otherTasks = tasks.filter(task => task.completed);

    setTasks([...reordered, ...otherTasks]); // Las tareas completadas se quedan igual
  };


  /* -------------------------------------------------------------------------------------------------------------------- */

  return (
    <Router>
      <div className="mainContainer">
        <header>
          <img src={logoFull} alt="Logo TaskQuest" />
          <h1></h1>
        </header>

        {/* Botones de acceso */}
        <div className="button-container">
          <Link to="/notas">
            <button className="openModalButton">
              <span className="plusIcon"><FontAwesomeIcon icon={faPlus} /></span> Notas
            </button>
          </Link>
          <button className="openModalButton" onClick={() => setShowModal(true)}>
            <span className="plusIcon"><FontAwesomeIcon icon={faFileLines} /></span> Añadir Tarea
          </button>
        </div>

        {/* Modal para añadir tarea */}
        {showModal && (
          <div className="modalOverlay">
            <div className="modalContent">
              <button onClick={() => setShowModal(false)} className="cancelButton"><FontAwesomeIcon icon={faXmark} /></button>
              <h2>Añadir Nueva Tarea:</h2>
              <TaskForm onAdd={addTask} />
            </div>
          </div>
        )}

        {/* Modal para editar tarea */}
        {showEditModal && (
          <EditTaskModal
            initialText={editTaskText}
            onSave={(newText) => editTask(editTaskId, newText)}
            onCancel={() => setShowEditModal(false)}
          />
        )}

        {/* Rutas de la app */}
        <Routes>
          <Route path="/notas" element={<NotesPage />} />
          <Route path="/" element={
            <div className="listContainer">
              <DragDropContext onDragEnd={handleDragEnd}>
                <TaskToDo
                  tasks={tasks.filter(task => !task.completed)}
                  onToggle={toggleTask}
                  onDelete={deleteTask}
                  onEdit={openEditModal}
                />
              </DragDropContext>

              <TaskDone
                tasks={tasks.filter(task => task.completed)}
                onToggle={toggleTask}
                onDelete={deleteTask}
              />
            </div>
          } />
        </Routes>

      </div>
    </Router>
  );
};

export default App;
