import React from 'react';
import TaskItem from './TaskItem'; // Importamos el componente TaskItem
import "./ListStyle.css";

const TaskToDo = ({ tasks, onToggle, onDelete, onEdit}) => {//las ppropiedades como argumentos
    const tasksToDo = tasks.filter(task => !task.completed);



  <ul className="listaTareas">
    {tasks.map((task) => (//esto es un array de tareas, y cada tarea tiene propiedades
      <TaskItem
        key={task.id}
        task={task}//la tarea en sí
        onToggle={onToggle} // Función para marcar como completada
        onDelete={onDelete} // Función para eliminar tarea
        onEdit={onEdit} // Función para editar tarea
      />

    ))}
  </ul>

};

export default TaskToDo;
