import React from 'react';
import "./ItemStyle.css";

const TaskItem = ({ task, onToggle, onDelete, onEdit, editable = true }) => (
  <li className="item">
    <div className="taskControls">
      <button className="dltButton" onClick={() => onDelete(task.id)}>üóëÔ∏è</button>

      {/* Bot√≥n Editar solo si editable */}
      {editable && (
        <button
          className="editButton"
          onClick={() => onEdit(task.id, task.text)} // Ya no hay prompt, solo modal
        >
          üñäÔ∏è Editar
        </button>
      )}

      <input
        type="checkbox"
        checked={task.completed}
        onChange={() => onToggle(task.id)}
      />

      <span className="taskText" style={{
        textDecoration: task.completed ? 'line-through' : 'none',
        textDecorationThickness: task.completed ? '4px' : 'none',
        color: task.completed ? '#888' : 'inherit'
      }}>
        {task.text}
      </span>
    </div>
  </li>
);

export default TaskItem;
