import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearchMinus, faSearchPlus, faTrash } from '@fortawesome/free-solid-svg-icons';

import './notesStyle.css';

const NoteItem = ({ note, onDelete, onToggleExpand, date }) => {
  return (

    <div className="noteItem">

      {note.date && (
  <p>Creada: {new Date(note.date).toLocaleString()}</p>
)}

      <p>{note.expanded ? note.content : note.content.substring(0, 120) + '...'}</p>
      <div className="btnContainer">

<h3
  className="expandButton"
  onClick={() => onToggleExpand(note.id)}
  style={{ cursor: 'pointer' }}
>
  <span className="expandIcon">
    <FontAwesomeIcon icon={note.expanded ? faSearchMinus : faSearchPlus} />
  </span>
  {note.expanded ? 'Ver menos' : 'Ver más'}
</h3>

<button
  className="delButton"
  onMouseDown={() => {
    note._deleteTimeout = setTimeout(() => onDelete(note.id), 800);
  }}
  onMouseUp={() => clearTimeout(note._deleteTimeout)}
  onMouseLeave={() => clearTimeout(note._deleteTimeout)}
  onTouchStart={() => {
    note._deleteTimeout = setTimeout(() => onDelete(note.id), 800);
  }}
  onTouchEnd={() => clearTimeout(note._deleteTimeout)}
>
  <span className="trashIcon"><FontAwesomeIcon icon={faTrash} /></span>Eliminar
</button>

</div>


    </div>
  );
};

export default NoteItem;
