import React from 'react';

const NoteItem = ({ note, onDelete, onToggleExpand }) => {
  return (
    <div className="noteItem">

      <p>{note.expanded ? note.content : note.content.substring(0, 50) + '...'}</p>
      <h3 onClick={() => onToggleExpand(note.id)}>
        {note.expanded ? 'Ver menos' : 'Ver más'}
      </h3>
      <button onClick={() => onDelete(note.id)}>Eliminar</button>
    </div>
  );
};

export default NoteItem;
