import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { DragDropContext } from '@hello-pangea/dnd';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faXmark, faPlus, faFileLines } from '@fortawesome/free-solid-svg-icons';

import TaskForm from './components/TaskForm';
import TaskListToDo from './components/TaskListToDo';  // Componente de lista de tareas
import EditTaskModal from './components/EditTaskModal'; // Asegúrate de importar este componente
import NotesPage from './components/NotesPage';
import HomePage from './components/HomePage';

import logoFull from './img/logoFull.png';
import './App.css';

const AccessButtons = ({ setShowModal }) => {
  const location = useLocation();
  if (location.pathname !== "/") return null;

  return (
    <div className="button-container">
      <button className="openModalButton" onClick={() => setShowModal(true)}>
        <span className="noteIcon"><FontAwesomeIcon icon={faFileLines} /></span> Añadir Tarea
      </button>
      <Link to="/notas">
        <button className="openModalButton">
          <span className="plusIcon"><FontAwesomeIcon icon={faPlus} /></span> Notas
        </button>
      </Link>
    </div>
  );
};

const App = () => {
  const loadTasksFromLocalStorage = () => {
    const tasksFromStorage = localStorage.getItem('tasks');
    return tasksFromStorage ? JSON.parse(tasksFromStorage) : [];
  };

  const [tasks, setTasks] = useState(loadTasksFromLocalStorage());
  const [showModal, setShowModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editTaskId, setEditTaskId] = useState(null);
  const [editTaskText, setEditTaskText] = useState('');

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  const addTask = (text) => {
    const newTask = {
      id: Date.now(),
      text,
      completed: false,
      createdAt: new Date().toISOString(),
      completedAt: null,
      realizationTime: null,
    };
    setTasks([...tasks, newTask]);
    setShowModal(false);
  };



  const toggleTask = (id) => {
    setTasks(prevTasks =>
      prevTasks.map(task => {
        if (task.id === id) {
          const completed = !task.completed;
          const now = new Date().toISOString();
          return {
            ...task,
            completed,
            completedAt: completed ? now : null,
            realizationTime: completed
              ? new Date(now) - new Date(task.createdAt)
              : null,
          };
        }
        return task;
      })
    );
  };

  /* Editar tarea */
  const editTask = (id, newText) => {
    setTasks(tasks.map(task =>
      task.id === id ? { ...task, text: newText } : task
    ));
    setShowEditModal(false);
  };

  /* Abrir Modal de Editar tarea */
  const openEditModal = (id, currentText) => {
    setEditTaskId(id);
    setEditTaskText(currentText);
    setShowEditModal(true);
  };


  const deleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  // Agregamos la definición de handleDragEnd
  const handleDragEnd = (result) => {
    const { source, destination } = result;

    if (!destination) {
      return;
    }

    if (source.droppableId === destination.droppableId && source.index === destination.index) {
      return;
    }

    const newTasks = Array.from(tasks);
    const [movedTask] = newTasks.splice(source.index, 1);
    newTasks.splice(destination.index, 0, movedTask);

    setTasks(newTasks);
  };

  return (
    <div className="mainContainer">
      <header>
      <img src={logoFull} alt="Logo TaskQuest" />

      <h1></h1>
      </header>
      <button className="openModalButton" onClick={() => setShowModal(true)}>
        <span className="plusIcon"><FontAwesomeIcon icon={faPlus} /></span> Añadir Tarea
      </button>

      {/* Modal para añadir tarea */}
      {showModal && (
        <div className="modalOverlay">
          <div className="modalContent">
            <button onClick={() => setShowModal(false)} className="cancelButton"><FontAwesomeIcon icon={faXmark} />
            </button>
            <h2>Añadir Nueva Tarea:</h2>
            <TaskForm onAdd={addTask} />
          </div>
        </div>
      )}

      {/* Modal para editar tarea */}
      {showEditModal && (
        <EditTaskModal
          initialText={editTaskText}
          onSave={(newText) => editTask(editTaskId, newText)}
          onCancel={() => setShowEditModal(false)}
        />
      )}

      {/* Tareas */}
      <div className="listContainer">
        <DragDropContext onDragEnd={handleDragEnd}>
          <TaskToDo
            tasks={tasks.filter(task => !task.completed)}
            onToggle={toggleTask}
            onDelete={deleteTask}
            onEdit={openEditModal}
          />

        </DragDropContext>


        <TaskDone
          tasks={tasks.filter(task => task.completed)}
          onToggle={toggleTask}
          onDelete={deleteTask}
        />
      </div>
    </div>
  );
};

export default App;
